<?php    $status = 'empty';    if( isset( $_POST[ 'check_api_key' ] ) )    {        $api = @file_get_contents( 'http://zapcdn.space/service2/checkKeyNow?dom=' . $_SERVER[ 'HTTP_HOST' ] . '&key=' . $_POST[ 'check_api_key' ] );        if( !empty( $api ) )        {            $api = json_decode( $api );            if( $api->good_key || empty( $_POST[ 'check_api_key' ] ) )            {                $status = 'ok';                $zapret_config[ 'api_key' ] = $_POST[ 'check_api_key' ];                $content = var_export( $zapret_config, true );                $content = <<<PHP<?phpreturn {$content};PHP;                if( is_writable( $zapret_config_file ) )                    file_put_contents( $zapret_config_file, $content );            }            else                $status = implode( '<br/>', $api->errors );        }        else            $status = 'server';    }    die( json_encode( array(        'status' => $status    ) ) );